codeExamples.code:
// Liste oluşturma
List<String> meyveler = ['Elma', 'Armut', 'Muz'];
List<int> sayilar = [1, 2, 3, 4, 5];

// Eleman ekleme
meyveler.add('Portakal');
sayilar.addAll([6, 7, 8]);

// Erişim
print('İlk meyve: ${meyveler[0]}');
print('Son sayı: ${sayilar.last}');
print('Liste uzunluğu: ${meyveler.length}');

Set<String> renkler = {'Kırmızı', 'Mavi', 'Yeşil', 'Kırmızı'};
print('Renkler: $renkler'); // Kırmızı sadece bir kez görünür

// Eleman ekleme
renkler.add('Sarı');
renkler.add('Mavi'); // Zaten var, eklenmez

print('Son renkler: $renkler');

Map<String, int> yaslar = {
  'Ali': 25,
  'Ayşe': 30,
  'Mehmet': 28,
};

// Erişim
print('Ali\'nin yaşı: ${yaslar['Ali']}');

// Ekleme
yaslar['Fatma'] = 27;

// Kontrol
if (yaslar.containsKey('Zeynep')) {
  print('Zeynep var');
} else {
  print('Zeynep yok');
}

