Map<String, int> ogrenciNotlari = {
  'Ali': 85,
  'Ayşe': 92,
  'Mehmet': 78,
};

int toplam = 0;
for (int not in ogrenciNotlari.values) {
  toplam += not;
}

double ortalama = toplam / ogrenciNotlari.length;
int enYuksek = ogrenciNotlari.values.reduce((a, b) => a > b ? a : b);

print('Ortalama: $ortalama');
print('En yüksek not: $enYuksek');
