codeExamples.code:
bool girisYapildi = true;
bool premiumKullanici = false;

if (girisYapildi) {
  print('Hoş geldiniz!');
  if (premiumKullanici) {
    print('Premium özelliklere erişebilirsiniz.');
  } else {
    print('Standart kullanıcı.');
  }
} else {
  print('Lütfen giriş yapın.');
}

